package com.techpakka.eerattupetta;

import android.content.Intent;
import android.support.annotation.NonNull;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.support.v7.widget.CardView;
import android.support.v7.widget.DefaultItemAnimator;
import android.support.v7.widget.GridLayoutManager;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;
import android.widget.ViewFlipper;

import com.android.volley.Cache;
import com.android.volley.Network;
import com.android.volley.RequestQueue;
import com.android.volley.toolbox.BasicNetwork;
import com.android.volley.toolbox.DiskBasedCache;
import com.android.volley.toolbox.HurlStack;
import com.bumptech.glide.Glide;
import com.google.firebase.firestore.DocumentSnapshot;
import com.google.firebase.firestore.FirebaseFirestore;
import com.techpakka.eerattupetta.Model.Data;

import java.util.ArrayList;
import java.util.List;

public class SthapanangalActivity extends AppCompatActivity {

    private RecyclerView recyclerView;
    ArrayList<Data> list = new ArrayList<>();
    private Adapter adapter;
    RequestQueue mRequestQueue;
    int index = 0;
    private ViewFlipper view_flipper;
    private final FirebaseFirestore db = FirebaseFirestore.getInstance();
    private String[] ad_images = new String[3];

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_sthapanangal);
        setUpFlipView();
        Cache cache = new DiskBasedCache(getCacheDir(), 1024 * 1024); // 1MB cap

// Set up the network to use HttpURLConnection as the HTTP client.
        Network network = new BasicNetwork(new HurlStack());

// Instantiate the RequestQueue with the cache and network.
        mRequestQueue = new RequestQueue(cache, network);

// Start the queue
        mRequestQueue.start();

        adapter = new Adapter(getSthapanangalData());
        recyclerView = findViewById(R.id.recyclerView);
        recyclerView.setHasFixedSize(false);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        ItemOffsetDecoration itemDecoration = new ItemOffsetDecoration(SthapanangalActivity.this, R.dimen.item_offset);
        recyclerView.addItemDecoration(itemDecoration);
        recyclerView.setItemAnimator(new DefaultItemAnimator());
        recyclerView.setAdapter(adapter);
    }
    private void setUpFlipView() {
        view_flipper = findViewById(R.id.view_flipper);
        db.collection("bottomads")
                .get()
                .addOnCompleteListener(task -> {

                    if (task.isSuccessful()) {
                        for (DocumentSnapshot document : task.getResult()) {
                            ad_images[index] = document.getString("image");
                            index++;
                        }
                        for(int i=0;i< ad_images.length;i++)
                        {
                            ImageView image = new ImageView(getApplicationContext());
                            image.setScaleType(ImageView.ScaleType.CENTER_CROP);
                            Glide.with(getApplicationContext()).load(ad_images[i]).into(image);
                            view_flipper.addView(image);
                        }
                        view_flipper.startFlipping();
                    } else {
                        Log.d("error", "Error getting documents.", task.getException());
                    }
                });
    }
    private List<Data> getSthapanangalData() {

        Data data = new Data();
        data.setSthapangal_name("വിദ്യഭ്യാസ സ്ഥാപനം ");
        data.setSthapanangal_image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlslMw8GhAfHK47yp1JEnnIE6AIzOztYi7i7pVARtMkBg8IAD_LA");
        list.add(data);
        Data data1 = new Data();
        data1.setSthapangal_name("സർക്കാർ");
        data1.setSthapanangal_image("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT2TsGc5I3uuyxq0r5Z9AdmeqXuHxmJZopiiWAfV-Av2bqPYj31");
        list.add(data1);
        Data data2 = new Data();
        data2.setSthapangal_name("മത");
        data2.setSthapanangal_image("data:image/png;base64,");
        list.add(data2);
        Data data3 = new Data();
        data3.setSthapangal_name("ബാങ്ക്");
        data3.setSthapanangal_image("data:image/jpeg;base64,");
        list.add(data3);
        Data data4 = new Data();
        data4.setSthapangal_name("ഫാക്ടറി");
        data4.setSthapanangal_image("data:image/jpeg;base64,");
        list.add(data4);
        return list;
    }

    public class Adapter extends RecyclerView.Adapter<Adapter.MyViewHolder>{
        List<Data> list;
        public Adapter(List<Data> list){
            this.list = list;
        }

        @NonNull
        @Override
        public Adapter.MyViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {
            View itemView = LayoutInflater.from(viewGroup.getContext())
                    .inflate(R.layout.content_sthapanangal, viewGroup, false);
            return new MyViewHolder(itemView);
        }

        @Override
        public void onBindViewHolder(@NonNull Adapter.MyViewHolder myViewHolder, int i) {
            Data data = list.get(i);
            myViewHolder.txt_sthapanam_name.setText(data.getSthapangal_name());
            Glide.with(getApplicationContext()).load(data.getSthapanangal_image()).into(myViewHolder.imageView);
            myViewHolder.container.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View view) {
                    if (i == 0){
                        Intent intent = new Intent(SthapanangalActivity.this,SthapanangalDetail.class);
                        intent.putExtra("id","1");
                        startActivity(intent);
                    }
                    if (i == 1){
                        Intent intent = new Intent(SthapanangalActivity.this,SthapanangalDetail.class);
                        intent.putExtra("id","2");
                        startActivity(intent);
                    }
                    if (i == 2){
                        Intent intent = new Intent(SthapanangalActivity.this,SthapanangalDetail.class);
                        intent.putExtra("id","3");
                        startActivity(intent);
                    }
                    if (i == 3){
                        Intent intent = new Intent(SthapanangalActivity.this,SthapanangalDetail.class);
                        intent.putExtra("id","4");
                        startActivity(intent);
                    }

                    if (i == 4){
                        Intent intent = new Intent(SthapanangalActivity.this,SthapanangalDetail.class);
                        intent.putExtra("id","5");
                        startActivity(intent);
                    }

                }
            });
        }

        @Override
        public int getItemCount() {
            return list.size();
        }
        public class MyViewHolder extends RecyclerView.ViewHolder {
            TextView txt_sthapanam_name;
            CardView container;
            ImageView imageView;
            public MyViewHolder(@NonNull View itemView) {
                super(itemView);
                container = itemView.findViewById(R.id.container);
                txt_sthapanam_name = itemView.findViewById(R.id.txt_sthapanam_name);
                imageView = itemView.findViewById(R.id.imageView);

            }
        }
    }

}
